mapper.sql:
FROM( SELECT TLM_KEYNO , @rownum:=@rownum+1 as COUNT , LEFT(TLM_REGDT,10) TLM_REGDT , IFNULL(U.UI_REP_NAME, TLM_REGNM) TLM_UI_NAME , M.TCGM_KEYNO , TLM_COMMENT , TLM_URL , TLM_USE_YN , TLM_TCGM_KEYNO , TLM_ORDER , (SELECT COUNT(*) FROM T_POPUPZONE_LIST P WHERE TLM_DEL_YN = 'N' AND TLM_TCGM_KEYNO = B.TLM_TCGM_KEYNO) LENGTH FROM T_POPUPZONE_LIST B LEFT JOIN U_USERINFO U ON B.TLM_REGNM = U.UI_KEYNO LEFT JOIN T_POPUPZONE_CATEGORY M ON B.TLM_TCGM_KEYNO = M.TCGM_KEYNO ,(SELECT @rownum:=0)TMP WHERE TLM_DEL_YN = 'N'
AND TLM_USE_YN = #{SHOW_USING_YN}
)AA WHERE 1=1
AND TCGM_KEYNO = #{category}
AND COUNT LIKE CONCAT('%',#{item.searchKeyword},'%')
AND TLM_COMMENT LIKE CONCAT('%',#{item.searchKeyword},'%')
AND TLM_URL LIKE CONCAT('%',#{item.searchKeyword},'%')
AND TLM_REGDT LIKE CONCAT('%',#{item.searchKeyword},'%')
AND ( COUNT LIKE CONCAT('%',#{item.searchKeyword},'%') OR TLM_COMMENT LIKE CONCAT('%',#{item.searchKeyword},'%') OR TLM_URL LIKE CONCAT('%',#{item.searchKeyword},'%') OR TLM_REGDT LIKE CONCAT('%',#{item.searchKeyword},'%') )

mapper.select:
	SELECT	*




 		ORDER BY	COUNT ${sortDirect}


 		ORDER BY	TLM_COMMENT ${sortDirect}


 		ORDER BY	TLM_URL ${sortDirect}


 		ORDER BY	TLM_REGDT ${sortDirect}


 		ORDER BY TLM_TCGM_KEYNO DESC, TLM_ORDER ASC, TLM_KEYNO DESC



	 	LIMIT  	#{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
		UPDATE 	T_POPUPZONE_LIST
 	SET 	TLM_USE_YN		=	#{TLM_USE_YN}
 	WHERE	TLM_KEYNO		=	#{TLM_KEYNO}


UPDATE T_POPUPZONE_LIST 
SET TLM_DEL_YN	=	'Y' , TLM_USE_YN ='N' 
WHERE	TLM_KEYNO		=	#{TLM_KEYNO}

    	UPDATE T_POPUPZONE_LIST
	   	SET	     TLM_ALT     	=    #{TLM_ALT}      
				,TLM_FS_KEYNO   =    #{TLM_FS_KEYNO}  
				,TLM_URL        =    #{TLM_URL}        
				,TLM_COMMENT   	=    #{TLM_COMMENT}     
				,TLM_TCGM_KEYNO =    #{TLM_TCGM_KEYNO}     
				,TLM_DATE_YN 	=    #{TLM_DATE_YN}     
				,TLM_STARTDT 	=    #{TLM_STARTDT}     
				,TLM_ENDT 		=    #{TLM_ENDT}     
    	WHERE  	TLM_KEYNO       =	#{TLM_KEYNO}

UPDATE T_POPUPZONE_LIST A SET TLM_ORDER = ( CASE WHEN TLM_KEYNO = #{TLM_KEYNO} THEN #{TLM_ORDER}
WHEN TLM_ORDER >= #{TLM_ORDER} AND TLM_ORDER <= #{TLM_ORDER_BEFORE} THEN TLM_ORDER + 1 WHEN TLM_ORDER >= #{TLM_ORDER_BEFORE} AND TLM_ORDER <= #{TLM_ORDER} THEN TLM_ORDER - 1
ELSE TLM_ORDER END ) WHERE TLM_TCGM_KEYNO = #{TLM_TCGM_KEYNO} AND TLM_DEL_YN = 'N'

mapper.select:

SELECT	COUNT(*) FROM T_POPUPZONE_LIST 
WHERE 	TLM_ORDER 		=	#{TLM_ORDER}
AND		TLM_DEL_YN		=	'N'
AND		TLM_TCGM_KEYNO	=	#{TLM_TCGM_KEYNO}

mapper.update:

UPDATE	T_POPUPZONE_LIST
SET 	TLM_ORDER		=	TLM_ORDER + 1
WHERE	TLM_ORDER		>=	#{TLM_ORDER}
AND 	TLM_DEL_YN		=	'N'
AND		TLM_TCGM_KEYNO	=	#{TLM_TCGM_KEYNO}

mapper.select:
	SELECT	*




 		ORDER BY	COUNT ${sortDirect}


 		ORDER BY	TCGM_TITLE ${sortDirect}


 		ORDER BY	TCGM_UI_NAME ${sortDirect}


 		ORDER BY	TCGM_REGDT ${sortDirect}


 		ORDER BY TCGM_KEYNO DESC



	 	LIMIT  	#{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
UPDATE T_POPUPZONE_CATEGORY
SET   TCGM_TITLE 		= #{TCGM_TITLE}
	, TCGM_FORM 		= #{TCGM_FORM}
	, TCGM_IMG_YN 		= #{TCGM_IMG_YN}
	, TCGM_IMG_WIDTH 	= #{TCGM_IMG_WIDTH}
	, TCGM_IMG_HEIGHT 	= #{TCGM_IMG_HEIGHT}
	, TCGM_IMG_RESIZE_YN = #{TCGM_IMG_RESIZE_YN}
WHERE TCGM_KEYNO 		= #{TCGM_KEYNO}

UPDATE T_POPUPZONE_CATEGORY
SET TCGM_DEL_YN = 'Y'
WHERE TCGM_KEYNO = #{TCGM_KEYNO}

UPDATE T_POPUPZONE_LIST 
SET TLM_DEL_YN	=	'Y'
WHERE	TLM_TCGM_KEYNO		=	#{TCGM_KEYNO}

mapper.select:
SELECT 	TCGM_FORM
FROM 	T_POPUPZONE_CATEGORY
WHERE 	TCGM_KEYNO = #{TCGM_KEYNO}
