mapper.select:
		SELECT 	*



      ORDER BY  FM_KEYNO ${sortDirect}


      ORDER BY  FM_WHERE_KEYS ${sortDirect}


      ORDER BY  FM_COMMENTS ${sortDirect}


      ORDER BY  FS_ORINMS ${sortDirect}


      ORDER BY  UI_ID ${sortDirect}


      ORDER BY  FM_REGDT ${sortDirect}


      ORDER BY  KEYNO DESC



      LIMIT   #{recordCountPerPage} OFFSET #{firstIndex}

SELECT FS.* , CONCAT(FS_FOLDER,FS_CHANGENM, '.',FS_EXT) THUMBNAIL_PATH , FS_PUBLIC_PATH THUMBNAIL_PUBLIC_PATH FROM S_COMMON_FILE_SUB FS WHERE
FS_KEYNO = #{FS_KEYNO}
FS_FM_KEYNO = #{FS_FM_KEYNO}

mapper.update:
UPDATE    S_COMMON_FILE_MAIN
SET    
  FM_REGNM      = #{FM_REGNM}

  , FM_WHERE_KEYS = #{FM_WHERE_KEYS}

  , FM_COMMENTS   = #{FM_COMMENTS}
WHERE   FM_KEYNO  =   #{FM_KEYNO}

    UPDATE    S_COMMON_FILE_SUB
    SET    
			  FS_FILE_SIZE  	= #{FS_FILE_SIZE}
			, FS_ORINM          = #{FS_ORINM}
			, FS_ORIWIDTH       = #{FS_ORIWIDTH}
			, FS_ORIHEIGHT      = #{FS_ORIHEIGHT}
			, FS_REGNM          = #{FS_REGNM}
			, FS_CHANGENM       = #{FS_CHANGENM}
			, FS_EXT            = #{FS_EXT}
			, FS_FOLDER		    = #{FS_FOLDER}
			, FS_STORAGE		= #{FS_STORAGE}
			, FS_PUBLIC_PATH	= #{FS_PUBLIC_PATH}
			, FS_REGDT      	= SYSDATE()

			, FS_FM_KEYNO        = #{FS_FM_KEYNO}


			, FS_ALT        = #{FS_ALT}


       		, FS_COMMENTS   = #{FS_COMMENTS}


       		, FS_THUMBNAIL   = #{FS_THUMBNAIL}

    WHERE   FS_KEYNO  =   #{FS_KEYNO}


    UPDATE    S_COMMON_FILE_SUB
    SET    
			FS_ALT        = #{FS_ALT}
			, FS_COMMENTS   = #{FS_COMMENTS}
			, FS_ORDER   = #{FS_ORDER}
    WHERE   FS_KEYNO  =   #{FS_KEYNO}
