mapper.select:
    	SELECT	* 



	 		ORDER BY	COUNT ${sortDirect}


	 		ORDER BY	UI_ID ${sortDirect}


	 		ORDER BY	UI_NAME ${sortDirect}


	 		ORDER BY	UIA_NAME ${sortDirect}


	 		ORDER BY	UI_REGDT ${sortDirect}


	 		ORDER BY	UI_LASTLOGIN ${sortDirect}


	 		ORDER BY	AUTH ${sortDirect}


	 		ORDER BY 	COUNT DESC



		LIMIT  	#{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
       	INSERT INTO U_USERINFO
		(
				  UI_KEYNO
				, UI_ID
				, UI_PASSWORD
				, UI_NAME
				, UI_EMAIL
				, UI_PHONE
				, UI_BIRTH
				, UI_ZENDER
				, UI_REGDT
				, UI_AUTH_YN
				, UI_REP_NAME
				, UI_PASSWORD_CHDT
		)
		VALUES
		(
				  #{UI_KEYNO}
				, #{UI_ID}
				, #{UI_PASSWORD}
				, #{UI_NAME}
				, #{UI_EMAIL}
				, #{UI_PHONE}
				, #{UI_BIRTH}
				, #{UI_ZENDER}
				, NOW()
				, #{UI_AUTH_YN}
				, #{UI_REP_NAME}
				, NOW()
		)

mapper.update:

    	UPDATE U_USERINFO
    	SET		  UI_NAME		=	#{UI_NAME}

    	         ,UI_EMAIL	    =	#{UI_EMAIL}

    	         ,UI_PHONE	    =	#{UI_PHONE}
    	         ,UI_BIRTH	    =	#{UI_BIRTH}
    	         ,UI_ZENDER	    =	#{UI_ZENDER}
    	         ,UI_AUTH_YN	=	#{UI_AUTH_YN}
    	         ,UI_REP_NAME	=	#{UI_REP_NAME}

    			, UI_PASSWORD	=	#{UI_PASSWORD}
    			, UI_PWD_INIT	=	'N'	


		    	, UI_DORMANCY	=	#{UI_DORMANCY}

    	WHERE	  UI_KEYNO		=	#{UI_KEYNO}

UPDATE	U_USERINFO
SET		  UI_PASSWORD	=	#{UI_PASSWORD}
		, UI_PWD_INIT	=	'N'	
		, UI_PASSWORD_CHDT = NOW()
WHERE	UI_KEYNO		=	#{UI_KEYNO}

UPDATE	U_USERINFO
SET		UI_PASSWORD_CHDT = DATE_ADD(NOW(), INTERVAL #{REVISE_DATE} DAY)
WHERE	UI_KEYNO		=	#{UI_KEYNO}

UPDATE U_USERINFO 
SET    UI_DELYN='Y'
WHERE  UI_KEYNO=#{UI_KEYNO}

