mapper.update:
UPDATE U_USERINFO
SET		  UI_PASSWORD	=	#{UI_PASSWORD}	
WHERE	  UI_KEYNO		=	#{UI_KEYNO}

UPDATE 	U_USERINFO
SET		UI_PASSWORD		=	#{UI_PASSWORD}
	  , UI_PWD_INIT		=	'N'	
WHERE	UI_KEYNO		=	#{UI_KEYNO}

mapper.insert:

    	INSERT INTO U_USERINFO_SETTING(
    		  US_TYPE
			, US_UIA_KEYNO
			, US_AUTH
			, US_REGEX
			, US_ID_FILTER
			, US_INFO1
			, US_INFO2
    	)
    	VALUES(
			  #{US_TYPE}
			, #{US_UIA_KEYNO}
			, #{US_AUTH}
			, #{US_REGEX}
			, #{US_ID_FILTER}
			, #{US_INFO1}
			, #{US_INFO2}
		)
		ON DUPLICATE KEY UPDATE 
			  US_UIA_KEYNO	=	#{US_UIA_KEYNO}
			, US_AUTH		=	#{US_AUTH}
			, US_REGEX		=	#{US_REGEX}
			, US_ID_FILTER	=	#{US_ID_FILTER}
			, US_INFO1		=	#{US_INFO1}
			, US_INFO2		=	#{US_INFO2}

mapper.select:
SELECT 	* 
FROM 	U_USERINFO
WHERE	UI_NAME		=	#{UI_NAME}
AND		UI_EMAIL	=	#{UI_EMAIL}
AND		UI_DELYN	=	'N'

SELECT 	* 
FROM 	U_USERINFO
WHERE	UI_ID		=	#{UI_ID}
AND		UI_EMAIL	=	#{UI_EMAIL}
AND		UI_DELYN	=	'N'

    	SELECT GROUP_CONCAT(result) FROM (
			SELECT 	'email' result
			FROM 	U_USERINFO
			WHERE	UI_EMAIL	=	#{UI_EMAIL}

			UNION

			SELECT 	'id' 
			FROM 	U_USERINFO
			WHERE	UI_ID	=	#{UI_ID}
		) A

SELECT 	COUNT(*) 
FROM 	U_USERINFO
WHERE	UI_ID	=	#{UI_ID}

mapper.update:
UPDATE U_USERINFO
SET		  UI_AUTH_YN		=	'Y'
WHERE	  UI_ID		=	#{UI_ID}

UPDATE U_USERINFO
SET		  UI_AUTH_DATA		=	#{UI_AUTH_DATA}
WHERE	  UI_KEYNO			=	#{UI_KEYNO}

UPDATE U_USERINFO
SET		  UI_DORMANCY	=	'N'
WHERE	  UI_ID		=	#{UI_ID}

UPDATE U_USERINFO
SET		UI_TRY_COUNT 	= 	#{UI_TRY_COUNT}
WHERE	UI_KEYNO = #{UI_KEYNO}

