mapper.insert:
	INSERT INTO S_RESOURCES_MANAGER(
		   RM_KEYNO
       	 , RM_MN_HOMEDIV_C
         , RM_SCOPE
         , RM_TITLE
         , RM_DATA
         , RM_REGDT
         , RM_REGNM
         , RM_FILE_NAME
         , RM_TYPE
         , RM_ORDER
	)
	VALUES(
		  #{RM_KEYNO}
		, #{RM_MN_HOMEDIV_C}
		, #{RM_SCOPE}
		, #{RM_TITLE}
		, #{RM_DATA}
		, NOW()
		, #{RM_REGNM}
		, #{RM_FILE_NAME}
		, #{RM_TYPE}
		, #{RM_ORDER}
	)

mapper.update:
        UPDATE  S_RESOURCES_MANAGER
		SET 	RM_ORDER 			= RM_ORDER + 1
		WHERE   	RM_TYPE 			= #{RM_TYPE}

		AND 	RM_MN_HOMEDIV_C 	= #{RM_MN_HOMEDIV_C}

		AND   	RM_DEL_YN 			= 'N'
		AND   	  RM_ORDER >= #{RM_ORDER}
		AND   	RM_KEYNO 			!= #{RM_KEYNO}

        UPDATE S_RESOURCES_MANAGER
		SET    RM_ORDER = #{RM_ORDER}
		WHERE  RM_KEYNO = #{RM_KEYNO}

mapper.select:
SELECT 		*
FROM 		S_RESOURCES_MANAGER
WHERE 		1=1

AND			RM_MN_HOMEDIV_C = #{MN_KEYNO}


AND   		RM_SCOPE 		= #{SCOPE}


AND   		RM_TYPE 		= #{RM_TYPE}

AND   		RM_DEL_YN 		= 'N'
ORDER BY 	RM_ORDER

SELECT RM_SCOPE FROM S_RESOURCES_MANAGER WHERE 1=1
AND RM_MN_HOMEDIV_C = #{MN_KEYNO}
AND RM_TYPE = #{RM_TYPE} AND RM_DEL_YN = 'N' GROUP BY RM_SCOPE

SELECT 	  (SELECT UI_NAME FROM U_USERINFO WHERE UI_KEYNO = RM.RM_REGNM) RM_REGNM
		, (SELECT UI_NAME FROM U_USERINFO WHERE UI_KEYNO = RM.RM_MODNM) RM_MODNM
		, DATE_FORMAT(RM.RM_REGDT, '%Y-%m-%d %H:%s:%m') RM_REGDT
		, DATE_FORMAT(RM.RM_MODDT, '%Y-%m-%d %H:%s:%m') RM_MODDT
		, RM.*
FROM 	S_RESOURCES_MANAGER RM
WHERE 	RM_DEL_YN = 'N'



AND		RM_MN_HOMEDIV_C = #{RM_MN_HOMEDIV_C}

AND  	RM_TYPE = #{RM_TYPE}


AND  	RM_KEYNO = #{RM_KEYNO}



AND		RM_SCOPE = #{RM_SCOPE}

SELECT 	  (SELECT UI_NAME FROM U_USERINFO WHERE UI_KEYNO = RM.RM_REGNM) RM_REGNM
		, (SELECT UI_NAME FROM U_USERINFO WHERE UI_KEYNO = RM.RM_MODNM) RM_MODNM
		, DATE_FORMAT(RM.RM_REGDT, '%Y-%m-%d %H:%s:%m') RM_REGDT
		, DATE_FORMAT(RM.RM_MODDT, '%Y-%m-%d %H:%s:%m') RM_MODDT
		, RM.*
FROM 	S_RESOURCES_MANAGER RM
WHERE 	RM_DEL_YN = 'N'
AND RM_MN_HOMEDIV_C = #{RM_MN_HOMEDIV_C}
AND RM_TYPE = #{RM_TYPE}

AND RM_SCOPE = #{RM_SCOPE}

SELECT IFNULL(MAX(RMH_VERSION),0)
FROM S_RESOURCES_MANAGER_HISTORY
WHERE RMH_RM_KEYNO = #{RM_KEYNO}

SELECT IFNULL(MAX(RMH_VERSION),0) historyVersion,  IFNULL(MAX(RMH_KEYNO),'') historyKey
FROM S_RESOURCES_MANAGER_HISTORY
WHERE RMH_RM_KEYNO = #{RM_KEYNO}

SELECT 		RMH_DATA, RMH_VERSION
FROM		S_RESOURCES_MANAGER_HISTORY
WHERE		RMH_KEYNO	=	#{RMH_KEYNO}

SELECT COUNT(*)
FROM S_RESOURCES_MANAGER_SUB
WHERE 	RMS_RM_KEYNO   =  #{RMS_RM_KEYNO}

SELECT 	GROUP_CONCAT(RMS_MN_KEYNO) MENUKEYS, RMS_MN_TYPE
FROM	S_RESOURCES_MANAGER_SUB
WHERE 	RMS_RM_KEYNO   = #{RMS_RM_KEYNO}

mapper.insert:
INSERT INTO S_RESOURCES_MANAGER_SUB(
	  RMS_RM_KEYNO
	, RMS_MN_KEYNO
	, RMS_MN_TYPE
	)
VALUES(
	  #{RMS_RM_KEYNO}
	 ,#{RMS_MN_KEYNO}
	 ,#{RMS_MN_TYPE}
	)

mapper.update:
UPDATE S_RESOURCES_MANAGER
SET RM_DEL_YN = 'Y'
WHERE 	RM_MN_HOMEDIV_C = #{RM_MN_HOMEDIV_C}

UPDATE S_RESOURCES_MANAGER_HISTORY
SET RMH_DEL_YN = 'Y'
WHERE 	RMH_RM_KEYNO = #{RM_KEYNO}
