mapper.insert:
-- LOG OFF
	INSERT INTO	S_COMMON_SEQ_CNT (
		SQ_TABLE_NM
	)
	VALUES (
		#{TableName}
	)

mapper.select:
	-- LOG OFF
 		 SELECT 	IFNULL(COUNT(*),0)
       	 FROM 		${TableName}
       	 WHERE		${TableCode} = #{CodeColumn}

mapper.update:

UPDATE 	 	 S_COMMON_SEQ_CNT 
SET			 SQ_CNT 		=	#{cnt} 
WHERE		 SQ_TABLE_NM 	= 	#{TableName}

    -- LOG OFF

    		SELECT `AUTO_INCREMENT`
			FROM  INFORMATION_SCHEMA.TABLES
			WHERE TABLE_SCHEMA = #{TABLE_SCHEMA}
			AND   TABLE_NAME   = #{TABLE_NAME}

    	ALTER TABLE ${TABLE_NAME} AUTO_INCREMENT = #{incrementVal}
