mapper.select:

	SELECT GROUP_CONCAT(MN_NAME) FROM (
       SELECT MN_NAME FROM (
         SELECT ID FROM
           (
           SELECT @r as ID,
           (
             SELECT @r := MN_MAINKEY FROM S_MENU_MANAGER WHERE MN_KEYNO = ID
           ) AS _PARENT_ID

           FROM (
             SELECT @r := #{MN_KEYNO}
           ) vars, S_MENU_MANAGER h
           WHERE @r <> ''
         ) A
       ) B
       JOIN S_MENU_MANAGER M
       ON	B.ID = M.MN_KEYNO
       ORDER BY M.MN_LEV
     ) C

SELECT SUM(CNT) COUNT FROM ( SELECT COUNT(*) CNT FROM B_BOARD_NOTICE B LEFT JOIN S_MENU_MANAGER M ON B.BN_MN_KEYNO = MN_KEYNO
LEFT JOIN U_USERINFO_AUTHORITY_ROLL UAR ON UAR.MN_KEYNO = B.BN_MN_KEYNO
WHERE
BN_TITLE LIKE CONCAT('%',#{keyword},'%') OR BN_CONTENTS_SEARCH LIKE CONCAT('%',#{keyword},'%')
AND BN_USE_YN = 'Y' AND BN_DEL_YN = 'N' AND MN_HOMEDIV_C = #{BN_MN_KEYNO} AND MN_USE_YN = 'Y' AND MN_DEL_YN='N' AND MN_SHOW_YN = 'Y'
AND UIR_KEYNO = (SELECT UIR_KEYNO FROM U_USERINFO_ROLL WHERE UIR_NAME='access') AND UIA_KEYNO = #{userAuth}
AND BN_REGDT >= #{searchBeginDate}
AND BN_REGDT >= #{searchBeginDate} AND BN_REGDT <= #{searchEndDate}
) A

SELECT GROUP_CONCAT(keyword SEPARATOR ' ') keyword , COUNT(*) searchCnt , A.*
, CASE WHEN FIND_IN_SET (#{userAuth} , (SELECT ( SELECT GROUP_CONCAT(A.UIA_KEYNO) FROM U_USERINFO_AUTHORITY A, U_USERINFO_AUTHORITY_ROLL BR WHERE BR.UIA_KEYNO = A.UIA_KEYNO AND BR.MN_KEYNO = (SELECT MN_KEYNO FROM S_MENU_MANAGER SM WHERE SM.MN_URL = A.MN_URL) AND BR.UIR_KEYNO = U.UIR_KEYNO ) AUTHORITY_LIST FROM U_USERINFO_ROLL U WHERE U.UIR_TYPE = 'SC_MYKFE' AND UIR_NAME = 'read') ) THEN 'permit' ELSE 'disallow' END readAuth
FROM (
SELECT #{keyword} keyword , M.MN_URL , B.BN_KEYNO , B.BN_TITLE , B.BN_CONTENTS_SEARCH , B.BN_REGDT , B.BN_MN_KEYNO FROM B_BOARD_NOTICE B LEFT JOIN S_MENU_MANAGER M ON B.BN_MN_KEYNO = M.MN_KEYNO
LEFT JOIN U_USERINFO_AUTHORITY_ROLL UAR ON UAR.MN_KEYNO = B.BN_MN_KEYNO
WHERE ( BN_TITLE LIKE CONCAT('%',#{keyword},'%') OR BN_CONTENTS_SEARCH LIKE CONCAT('%',#{keyword},'%') ) AND BN_USE_YN = 'Y' AND BN_DEL_YN = 'N' AND MN_USE_YN = 'Y' AND MN_DEL_YN='N' AND MN_SHOW_YN = 'Y' AND MN_HOMEDIV_C = #{BN_MN_KEYNO}
AND UIR_KEYNO = (SELECT UIR_KEYNO FROM U_USERINFO_ROLL WHERE UIR_NAME='access') AND UIA_KEYNO = #{userAuth}
AND BN_REGDT >= #{searchBeginDate}
AND BN_REGDT >= #{searchBeginDate} AND BN_REGDT <= #{searchEndDate}
) A GROUP BY BN_KEYNO
ORDER BY searchCnt DESC, BN_REGDT DESC
ORDER BY BN_TITLE
ORDER BY BN_REGDT DESC
ORDER BY searchCnt DESC, BN_REGDT DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) FROM B_BOARD_NOTICE BN LEFT JOIN S_COMMON_FILE_MAIN FM ON BN.BN_FM_KEYNO = FM.FM_KEYNO LEFT JOIN S_COMMON_FILE_SUB FS ON FM.FM_KEYNO = FS.FS_FM_KEYNO LEFT JOIN S_MENU_MANAGER M ON BN.BN_MN_KEYNO = M.MN_KEYNO
LEFT JOIN U_USERINFO_AUTHORITY_ROLL UAR ON UAR.MN_KEYNO = BN.BN_MN_KEYNO
WHERE
FS.FS_ORINM LIKE CONCAT('%',#{keyword},'%')
AND BN_USE_YN = 'Y' AND BN_DEL_YN = 'N' AND MN_USE_YN = 'Y' AND MN_DEL_YN = 'N' AND MN_SHOW_YN = 'Y' AND MN_HOMEDIV_C = #{BN_MN_KEYNO}
AND UIR_KEYNO = (SELECT UIR_KEYNO FROM U_USERINFO_ROLL WHERE UIR_NAME='access') AND UIA_KEYNO = #{userAuth}
AND BN_MN_KEYNO IS NOT NULL
AND FS_REGDT >= #{searchBeginDate}
AND FS_REGDT >= #{searchBeginDate} AND FS_REGDT <= #{searchEndDate}

SELECT GROUP_CONCAT(keyword SEPARATOR ' ') keyword , COUNT(*) searchCnt , A.*
, CASE WHEN FIND_IN_SET (#{userAuth} , (SELECT ( SELECT GROUP_CONCAT(A.UIA_KEYNO) FROM U_USERINFO_AUTHORITY A, U_USERINFO_AUTHORITY_ROLL BR WHERE BR.UIA_KEYNO = A.UIA_KEYNO AND BR.MN_KEYNO = (SELECT MN_KEYNO FROM S_MENU_MANAGER SM WHERE SM.MN_URL = A.MN_URL) AND BR.UIR_KEYNO = U.UIR_KEYNO ) AUTHORITY_LIST FROM U_USERINFO_ROLL U WHERE U.UIR_TYPE = 'SC_MYKFE' AND UIR_NAME = 'read') ) THEN 'permit' ELSE 'disallow' END readAuth , CASE WHEN FIND_IN_SET (#{userAuth} , (SELECT ( SELECT GROUP_CONCAT(A.UIA_KEYNO) FROM U_USERINFO_AUTHORITY A, U_USERINFO_AUTHORITY_ROLL BR WHERE BR.UIA_KEYNO = A.UIA_KEYNO AND BR.MN_KEYNO = (SELECT MN_KEYNO FROM S_MENU_MANAGER SM WHERE SM.MN_URL = A.MN_URL) AND BR.UIR_KEYNO = U.UIR_KEYNO ) AUTHORITY_LIST FROM U_USERINFO_ROLL U WHERE U.UIR_TYPE = 'SC_MYKFE' AND UIR_NAME = 'download') ) THEN 'permit' ELSE 'disallow' END downAuth
FROM (
SELECT BN.* , FS.* , M.MN_URL , CONCAT(FS.FS_FOLDER, FS.FS_CHANGENM , '.' , FS.FS_EXT) AS FS_PATH , #{keyword} keyword FROM B_BOARD_NOTICE BN LEFT JOIN S_COMMON_FILE_MAIN FM ON BN.BN_FM_KEYNO = FM.FM_KEYNO LEFT JOIN S_COMMON_FILE_SUB FS ON FM.FM_KEYNO = FS.FS_FM_KEYNO LEFT JOIN S_MENU_MANAGER M ON BN.BN_MN_KEYNO = M.MN_KEYNO
LEFT JOIN U_USERINFO_AUTHORITY_ROLL UAR ON UAR.MN_KEYNO = BN.BN_MN_KEYNO
WHERE FS.FS_ORINM LIKE CONCAT('%',#{keyword},'%') AND BN_USE_YN = 'Y' AND BN_DEL_YN = 'N' AND MN_USE_YN = 'Y' AND MN_DEL_YN = 'N' AND MN_SHOW_YN = 'Y' AND MN_HOMEDIV_C = #{BN_MN_KEYNO}
AND UIR_KEYNO = (SELECT UIR_KEYNO FROM U_USERINFO_ROLL WHERE UIR_NAME='access') AND UIA_KEYNO = #{userAuth}
AND BN_MN_KEYNO IS NOT NULL
AND FS_REGDT >= #{searchBeginDate}
AND FS_REGDT >= #{searchBeginDate} AND FS_REGDT <= #{searchEndDate}
) A GROUP BY FS_KEYNO
ORDER BY searchCnt DESC, FS_REGDT DESC
ORDER BY FS_ORINM
ORDER BY FS_REGDT DESC
ORDER BY searchCnt DESC, FS_REGDT DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) FROM T_DEPARTMENT_USER_MANAGER DU LEFT JOIN T_DEPARTMENT_MANAGER DN ON DN.DN_KEYNO = DU.DU_DN_KEYNO WHERE
( DU_NAME LIKE CONCAT('%',#{keyword},'%') OR DU_ROLE LIKE CONCAT('%',#{keyword},'%') OR DU_CONTENTS LIKE CONCAT('%',#{keyword},'%') OR DU_TEL LIKE CONCAT('%',#{keyword},'%') ) AND DU_DEL_YN = 'N' AND DN.DN_HOMEDIV_C = #{BN_MN_KEYNO}
AND DU_DEL_YN = 'N'

SELECT A.* FROM (
SELECT #{keyword} keyword , DU.DU_NAME , DU.DU_ROLE , DU.DU_CONTENTS , DU.DU_TEL FROM T_DEPARTMENT_USER_MANAGER DU LEFT JOIN T_DEPARTMENT_MANAGER DN ON DN.DN_KEYNO = DU.DU_DN_KEYNO WHERE ( DU_NAME LIKE CONCAT('%',#{keyword},'%') OR DU_ROLE LIKE CONCAT('%',#{keyword},'%') OR DU_CONTENTS LIKE CONCAT('%',#{keyword},'%') OR DU_TEL LIKE CONCAT('%',#{keyword},'%') ) AND DU_DEL_YN = 'N' AND DN.DN_HOMEDIV_C = #{BN_MN_KEYNO}
) A
ORDER BY DU_NAME DESC, DU_CONTENTS DESC, DU_ROLE DESC
ORDER BY DU_CONTENTS DESC, DU_NAME DESC, DU_ROLE DESC
ORDER BY DU_NAME DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
