mapper.select:
    	SELECT	* 



	 		ORDER BY	COUNT ${sortDirect}


	 		ORDER BY	DPP_AREA ${sortDirect}


	 		ORDER BY	DPP_NAME ${sortDirect}


	 		ORDER BY	DPP_DATE ${sortDirect}


	 		ORDER BY 	COUNT DESC



		LIMIT  	#{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
       	INSERT INTO dy_power_plant
		(
				  DPP_NAME
				, DPP_IMG
				, DPP_X_LOCATION
				, DPP_Y_LOCATION
				, DPP_INVER_COUNT
				, DPP_AREA
		)
		VALUES
		(
	              #{DPP_NAME}
				, #{DPP_IMG}
				, #{DPP_X_LOCATION}
				, #{DPP_Y_LOCATION}
				, #{DPP_INVER_COUNT}
				, #{DPP_AREA}
		)
