mapper.insert:
	INSERT INTO U_USERINFO_AUTHORITY
	(
			  UIA_KEYNO
			, UIA_NAME
			, UIA_SYSTEM
			, UIA_MAINKEY
			, UIA_DEPTH
			, UIA_ORDER
			, UIA_DIVISION
			, UIA_HOMEKEY
	)
	VALUES
	(
   		   	  #{UIA_KEYNO}
			, #{UIA_NAME}
			, #{UIA_SYSTEM}
			, #{UIA_MAINKEY}
			, #{UIA_DEPTH}


			, (SELECT NVL(MAX(UIA_ORDER),0) + 1 FROM U_USERINFO_AUTHORITY WHERE UIA_MAINKEY = #{UIA_MAINKEY})


			, #{UIA_ORDER}	


			, #{UIA_DIVISION}
			, #{UIA_HOMEKEY}
	)

		INSERT ALL

		INTO U_USERINFO_AUTHORITY
		(
			  UIA_KEYNO
			, UIA_NAME
			, UIA_SYSTEM
			, UIA_MAINKEY
			, UIA_DEPTH
			, UIA_ORDER
			, UIA_DIVISION
		)
		VALUES
        (
   		   	  #{item.UIA_KEYNO}
			, #{item.UIA_NAME}
			, #{item.UIA_SYSTEM}
			, #{item.UIA_MAINKEY}
			, #{item.UIA_DEPTH}
			, #{item.UIA_ORDER}
			, #{item.UIA_DIVISION}
		)

        SELECT * FROM DUAL

mapper.select:
SELECT	COUNT(*)
FROM	U_USERINFO_AUTHORITY
WHERE	UIA_KEYNO	=	#{UIA_KEYNO}


AND		(UIA_MAINKEY	IS	NULL or UIA_MAINKEY = '')	


AND		UIA_MAINKEY	=	#{UIA_MAINKEY}


AND		UIA_DEPTH	=	#{UIA_DEPTH}

SELECT	UIA_KEYNO
FROM	U_USERINFO_MEMBER_AUTHORITY 
WHERE	UI_KEYNO	=	#{UI_KEYNO}

