mapper.sql:
FROM ( SELECT AA.*, ROW_NUMBER() OVER(
ORDER BY COUNT ${sortDirect}
ORDER BY MN_HOMEDIV_NAME ${sortDirect}
ORDER BY MN_NAME ${sortDirect}
ORDER BY TOTAL_CNT ${sortDirect}
ORDER BY SCORE_DATA5 ${sortDirect}
ORDER BY SCORE_DATA4 ${sortDirect}
ORDER BY SCORE_DATA3 ${sortDirect}
ORDER BY SCORE_DATA2 ${sortDirect}
ORDER BY SCORE_DATA1 ${sortDirect}
ORDER BY RESULT_DATA ${sortDirect}
ORDER BY COUNT
) RNUM FROM ( SELECT AB.*, ROWNUM AS COUNT FROM ( SELECT TPS_MN_KEYNO , B.MN_NAME , C.MN_NAME MN_HOMEDIV_NAME , TOTAL || '명' TOTAL_CNT , SCORE1 || '명' || CASE SCORE1 WHEN 0 THEN '' ELSE '(' || ROUND(SCORE1/TOTAL*100,2) || '%)' END SCORE_DATA1 , SCORE2 || '명' || CASE SCORE2 WHEN 0 THEN '' ELSE '(' || ROUND(SCORE2/TOTAL*100,2) || '%)' END SCORE_DATA2 , SCORE3 || '명' || CASE SCORE3 WHEN 0 THEN '' ELSE '(' || ROUND(SCORE3/TOTAL*100,2) || '%)' END SCORE_DATA3 , SCORE4 || '명' || CASE SCORE4 WHEN 0 THEN '' ELSE '(' || ROUND(SCORE4/TOTAL*100,2) || '%)' END SCORE_DATA4 , SCORE5 || '명' || CASE SCORE5 WHEN 0 THEN '' ELSE '(' || ROUND(SCORE5/TOTAL*100,2) || '%)' END SCORE_DATA5 , CASE WHEN SCORE5 >= SCORE4 AND SCORE5 >= SCORE3 AND SCORE5 >= SCORE2 AND SCORE5 >= SCORE1 THEN '매우 만족' WHEN SCORE4 >= SCORE3 AND SCORE4 >= SCORE2 AND SCORE4 >= SCORE1 THEN '만족' WHEN SCORE3 >= SCORE2 AND SCORE3 >= SCORE1 THEN '보통' WHEN SCORE2 >= SCORE1 THEN '불만족' ELSE '매우 불만족' END RESULT_DATA FROM ( SELECT TPS_MN_KEYNO , COUNT(TPS_SCORE_NAME) AS TOTAL , COUNT(CASE TPS_SCORE WHEN 1 THEN 1 ELSE NULL END ) SCORE1 , COUNT(CASE TPS_SCORE WHEN 2 THEN 1 ELSE NULL END ) SCORE2 , COUNT(CASE TPS_SCORE WHEN 3 THEN 1 ELSE NULL END ) SCORE3 , COUNT(CASE TPS_SCORE WHEN 4 THEN 1 ELSE NULL END ) SCORE4 , COUNT(CASE TPS_SCORE WHEN 5 THEN 1 ELSE NULL END ) SCORE5 FROM T_PAGE_RESEARCH_MANAGER TPR LEFT JOIN S_MENU_MANAGER TMM ON TPR.TPS_MN_KEYNO = TMM.MN_KEYNO GROUP BY TPS_MN_KEYNO ) A LEFT JOIN S_MENU_MANAGER B ON A.TPS_MN_KEYNO = B.MN_KEYNO LEFT JOIN S_MENU_MANAGER C
ON (B.MN_HOMEDIV_C = C.MN_KEYNO AND B.MN_HOMEDIV_C IS NOT NULL)
ON (B.MN_HOMEDIV_C = C.MN_KEYNO AND B.MN_HOMEDIV_C = #{HOMEDIV_C})
LEFT JOIN U_USERINFO_AUTHORITY_ROLL UAR ON UAR.MN_KEYNO = A.TPS_MN_KEYNO
WHERE UAR.UIA_KEYNO = #{UIA_KEYNO}
) AB WHERE 1=1 AND AB.MN_HOMEDIV_NAME IS NOT NULL
ORDER BY TOTAL_CNT DESC
)AA WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND MN_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND TOTAL_CNT LIKE '%' || #{item.searchKeyword} || '%'
AND SCORE_DATA5 LIKE '%' || #{item.searchKeyword} || '%'
AND SCORE_DATA4 LIKE '%' || #{item.searchKeyword} || '%'
AND SCORE_DATA3 LIKE '%' || #{item.searchKeyword} || '%'
AND SCORE_DATA2 LIKE '%' || #{item.searchKeyword} || '%'
AND SCORE_DATA1 LIKE '%' || #{item.searchKeyword} || '%'
AND RESULT_DATA LIKE '%' || #{item.searchKeyword} || '%'
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR MN_NAME LIKE '%' || #{item.searchKeyword} || '%' OR TOTAL_CNT LIKE '%' || #{item.searchKeyword} || '%' OR SCORE_DATA5 LIKE '%' || #{item.searchKeyword} || '%' OR SCORE_DATA4 LIKE '%' || #{item.searchKeyword} || '%' OR SCORE_DATA3 LIKE '%' || #{item.searchKeyword} || '%' OR SCORE_DATA2 LIKE '%' || #{item.searchKeyword} || '%' OR SCORE_DATA1 LIKE '%' || #{item.searchKeyword} || '%' OR RESULT_DATA LIKE '%' || #{item.searchKeyword} || '%' )
) BB

FROM ( SELECT AB.* , ROW_NUMBER() OVER (
ORDER BY COUNT ${sortDirect}
ORDER BY TPS_SCORE_NAME ${sortDirect}
ORDER BY TPS_IP ${sortDirect}
ORDER BY TPS_COMMENT ${sortDirect}
ORDER BY TPS_REGDT ${sortDirect}
ORDER BY COUNT
) RNUM FROM ( SELECT ROWNUM AS COUNT , TO_CHAR(TPR.TPS_REGDT, 'YYYY-MM-dd') AS TPS_REGDT , TPS_IP , TPS_COMMENT , TPS_SCORE_NAME FROM T_PAGE_RESEARCH_MANAGER TPR WHERE TPS_MN_KEYNO = #{TPS_MN_KEYNO} ORDER BY TPS_REGDT DESC ) AB WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_SCORE_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_IP LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_COMMENT LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR TPS_SCORE_NAME LIKE '%' || #{item.searchKeyword} || '%' OR TPS_IP LIKE '%' || #{item.searchKeyword} || '%' OR TPS_COMMENT LIKE '%' || #{item.searchKeyword} || '%' OR TPS_REGDT LIKE '%' || #{item.searchKeyword} || '%' )
)AA

mapper.select:
SELECT    * 


    WHERE 1=1
    AND     RNUM BETWEEN  #{firstIndex}+1 AND #{lastIndex}

mapper.sql:
FROM ( SELECT AB.*, ROW_NUMBER() OVER (
ORDER BY COUNT ${sortDirect}
ORDER BY MN_NAME ${sortDirect}
ORDER BY TPS_SCORE_NAME ${sortDirect}
ORDER BY TPS_IP ${sortDirect}
ORDER BY TPS_COMMENT ${sortDirect}
ORDER BY TPS_REGDT ${sortDirect}
ORDER BY COUNT
) RNUM FROM ( SELECT (RANK() OVER(ORDER BY TPR.TPS_REGDT DESC)) COUNT , SUBSTR(TPR.TPS_REGDT, 1, 10) AS TPS_REGDT , TPS_IP , TPS_COMMENT , TPS_SCORE_NAME , TPS_MN_KEYNO , TMM.MN_NAME AS MN_NAME FROM T_PAGE_RESEARCH_MANAGER TPR LEFT JOIN S_MENU_MANAGER TMM ON TPR.TPS_MN_KEYNO = TMM.MN_KEYNO
LEFT JOIN U_USERINFO_AUTHORITY_ROLL UAR ON UAR.MN_KEYNO = TPR.TPS_MN_KEYNO
WHERE MN_NAME IS NOT NULL
AND UAR.UIA_KEYNO = #{UIA_KEYNO}
AND DBMS_LOB.GETLENGTH(TPS_COMMENT) != 0 AND TMM.MN_HOMEDIV_C = #{HOMEDIV_C} ORDER BY TPS_REGDT DESC ) AB WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND MN_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_SCORE_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_IP LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_COMMENT LIKE '%' || #{item.searchKeyword} || '%'
AND TPS_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR TPS_SCORE_NAME LIKE '%' || #{item.searchKeyword} || '%' OR TPS_IP LIKE '%' || #{item.searchKeyword} || '%' OR TPS_COMMENT LIKE '%' || #{item.searchKeyword} || '%' OR MN_NAME LIKE '%' || #{item.searchKeyword} || '%' OR TPS_REGDT LIKE '%' || #{item.searchKeyword} || '%' )
) BB

mapper.select:
   		SELECT COUNT(*)
	    FROM T_PAGE_RESEARCH_MANAGER
	    WHERE TPS_IP = #{TPS_IP}
	    AND TPS_MN_KEYNO =  #{TPS_MN_KEYNO}
	    AND substr(TPS_REGDT, 0, 10) = substr(SYSDATE, 0, 10)

mapper.sql:
FROM( SELECT AB.* , ROW_NUMBER() OVER(
ORDER BY COUNT ${sortDirect}
ORDER BY PRS_SUBJECT ${sortDirect}
ORDER BY PRS_REGNM ${sortDirect}
ORDER BY PRS_REGDT ${sortDirect}
ORDER BY PRS_MODNM ${sortDirect}
ORDER BY PRS_MODDT ${sortDirect}
ORDER BY PRS_USE ${sortDirect}
ORDER BY PRS_KEYNO DESC
)RNUM FROM( SELECT AA.*, ROWNUM AS COUNT FROM ( SELECT PRS_KEYNO , SUBSTR(PRS_REGDT,0, 10) PRS_REGDT , SUBSTR(PRS_MODDT,0, 10) PRS_MODDT , NVL(U.UI_REP_NAME,PRS_REGNM) PRS_UI_NAME , NVL(U2.UI_REP_NAME,PRS_MODNM) PRS_MODNM , PRS_SUBJECT , DECODE((SELECT COUNT(*) FROM S_HOME_MANAGER WHERE HM_RESEARCH_SKIN = PRS_KEYNO), 0, 'N', 'Y') PRS_USE FROM T_PAGE_RESEARCH_SKIN P LEFT JOIN U_USERINFO U ON P.PRS_REGNM = U.UI_ID LEFT JOIN U_USERINFO U2 ON P.PRS_MODNM = U2.UI_ID WHERE PRS_DEL_YN = 'N' )AA ) AB WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND PRS_SUBJECT LIKE '%' || #{item.searchKeyword} || '%'
AND PRS_REGNM LIKE '%' || #{item.searchKeyword} || '%'
AND PRS_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND PRS_MODNM LIKE '%' || #{item.searchKeyword} || '%'
AND PRS_MODDT LIKE '%' || #{item.searchKeyword} || '%'
AND PRS_USE LIKE '%' || #{item.searchKeyword} || '%'
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR PRS_SUBJECT LIKE '%' || #{item.searchKeyword} || '%' OR PRS_REGNM LIKE '%' || #{item.searchKeyword} || '%' OR PRS_REGDT LIKE '%' || #{item.searchKeyword} || '%' OR PRS_MODNM LIKE '%' || #{item.searchKeyword} || '%' OR PRS_MODDT LIKE '%' || #{item.searchKeyword} || '%' OR PRS_USE LIKE '%' || #{item.searchKeyword} || '%' )
) BB

mapper.update:
UPDATE T_PAGE_RESEARCH_SKIN
SET   PRS_SUBJECT = #{PRS_SUBJECT}
	, PRS_FORM = #{PRS_FORM}
	, PRS_MODDT = SYSDATE
	, PRS_MODNM = #{PRS_MODNM}
WHERE PRS_KEYNO = #{PRS_KEYNO}

UPDATE T_PAGE_RESEARCH_SKIN
SET PRS_DEL_YN = 'Y'
WHERE PRS_KEYNO = #{PRS_KEYNO}

mapper.select:
SELECT NVL(MAX(PRSH_VERSION),0)
FROM T_PAGE_RESEARCH_SKIN_HISTORY
WHERE PRSH_PRS_KEYNO = #{PRS_KEYNO}

mapper.insert:
INSERT INTO T_PAGE_RESEARCH_SKIN_HISTORY(
	 PRSH_KEYNO
	,PRSH_PRS_KEYNO
	,PRSH_DATA
	,PRSH_STDT
	,PRSH_ENDT
	,PRSH_MODNM
	,PRSH_VERSION
	,PRSH_COMMENT
	)
VALUES(
	 #{PRSH_KEYNO}
	,#{PRSH_PRS_KEYNO}
	,#{PRSH_DATA}
	,TO_DATE(#{PRSH_STDT},'YYYY-MM-DD HH24:MI:SS')
	,SYSDATE
	,#{PRSH_MODNM}
	,#{PRSH_VERSION}
	,#{PRSH_COMMENT}
	)

mapper.select:
SELECT 		*
FROM		T_PAGE_RESEARCH_SKIN_HISTORY
WHERE		PRSH_KEYNO	=	#{PRSH_KEYNO}

SELECT 	COUNT(*)
FROM 	S_HOME_MANAGER 
WHERE 	HM_RESEARCH_SKIN = #{PRS_KEYNO}

SELECT     PRS_FORM
FROM     T_PAGE_RESEARCH_SKIN
WHERE     PRS_KEYNO = #{PRS_KEYNO}
