mapper.sql:
FROM( SELECT AA.* , ROW_NUMBER() OVER (
ORDER BY COUNT ${sortDirect}
ORDER BY BMM_SUBJECT ${sortDirect}
ORDER BY BMM_HOME_NAME ${sortDirect}
ORDER BY BMM_MN_NAME ${sortDirect}
ORDER BY BMM_SIZE ${sortDirect}
ORDER BY BMM_UI_NAME ${sortDirect}
ORDER BY BMM_REGDT ${sortDirect}
ORDER BY BMM_KEYNO DESC
) RNUM FROM( SELECT BMM_KEYNO , ROWNUM COUNT , TO_CHAR(BMM_REGDT,'YYYY-MM-dd') BMM_REGDT , NVL(U.UI_REP_NAME,BMM_REGNM) BMM_UI_NAME , M2.MN_NAME BMM_HOME_NAME , BMM_SIZE , M1.MN_NAME BMM_MN_NAME , BMM_SUBJECT FROM B_BOARD_MAINMINI B LEFT JOIN U_USERINFO U ON B.BMM_REGNM = U.UI_KEYNO LEFT JOIN S_MENU_MANAGER M1 ON B.BMM_MN_KEYNO = M1.MN_KEYNO LEFT JOIN S_MENU_MANAGER M2 ON B.BMM_MN_HOMEDIV_C = M2.MN_KEYNO WHERE BMM_DELYN = 'N' )AA WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND BMM_SUBJECT LIKE '%' || #{item.searchKeyword} || '%'
AND BMM_HOME_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND BMM_MN_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND BMM_SIZE = #{item.searchKeyword}
AND BMM_UI_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND BMM_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR BMM_SUBJECT LIKE '%' || #{item.searchKeyword} || '%' OR BMM_HOME_NAME LIKE '%' || #{item.searchKeyword} || '%' OR BMM_MN_NAME LIKE '%' || #{item.searchKeyword} || '%' OR BMM_SIZE = #{item.searchKeyword} OR BMM_UI_NAME LIKE '%' || #{item.searchKeyword} || '%' OR BMM_REGDT LIKE '%' || #{item.searchKeyword} || '%' )
) AAA

mapper.select:
	SELECT	*


	WHERE 1=1
 	AND RNUM BETWEEN #{firstIndex}+1 AND #{lastIndex}

SELECT BMM_KEYNO , 'BMM'||TO_NUMBER(SUBSTR(BMM_KEYNO,-10)) KEYNO , TO_CHAR(BMM_REGDT,'YYYY-MM-dd') BMM_REGDT , NVL(U.UI_REP_NAME,BMM_REGNM) BMM_UI_NAME , M2.MN_NAME BMM_HOME_NAME , M2.MN_URL BMM_MN_HOMEDIV_URL , BMM_SIZE , M1.MN_NAME BMM_MNNAME , BMM_FORM , BMM_MN_HOMEDIV_C , BMM_MN_KEYNO , M3.HM_TILES BMM_MN_HOMEDIV_TILES , BMM_SUBJECT , BMM_SORT_COLUMN , BMM_SORT_DIRECTION FROM B_BOARD_MAINMINI B LEFT JOIN U_USERINFO U ON B.BMM_REGNM = U.UI_KEYNO LEFT JOIN S_MENU_MANAGER M1 ON B.BMM_MN_KEYNO = M1.MN_KEYNO LEFT JOIN S_MENU_MANAGER M2 ON B.BMM_MN_HOMEDIV_C = M2.MN_KEYNO LEFT JOIN S_HOME_MANAGER M3 ON B.BMM_MN_HOMEDIV_C = M3.HM_MN_HOMEDIV_C WHERE BMM_DELYN = 'N'
AND BMM_MN_HOMEDIV_C = #{BMM_MN_HOMEDIV_C}
AND BMM_KEYNO = #{BMM_KEYNO}

mapper.update:
    	UPDATE B_BOARD_MAINMINI
    	SET   BMM_SIZE = #{BMM_SIZE}
    		, BMM_FORM = #{BMM_FORM}
    		, BMM_SUBJECT = #{BMM_SUBJECT}
    		, BMM_SORT_COLUMN = #{BMM_SORT_COLUMN}
			, BMM_SORT_DIRECTION = #{BMM_SORT_DIRECTION}
    	WHERE BMM_KEYNO = #{BMM_KEYNO}

UPDATE B_BOARD_MAINMINI
SET BMM_DELYN = 'Y'
WHERE BMM_KEYNO = #{BMM_KEYNO}

mapper.select:
    	SELECT *
    	FROM (SELECT	*
	    	FROM(
				SELECT 		BN_KEYNO
							, NVL(U.UI_REP_NAME,BN_REGNM) BN_REGNM
							, TO_CHAR(BN_REGDT,'YYYY-MM-dd') BN_REGDT
							, FS.FS_PUBLIC_PATH THUMBNAIL_PUBLIC_PATH
							, (FS.FS_FOLDER || FS.FS_CHANGENM || '.' || FS.FS_EXT) AS THUMBNAIL_PATH




							,  SC${column.KEYNO}.SC_CODENM COLUMN${column.KEYNO}



							, (
								SELECT LISTAGG(SC_CODENM,', ') WITHIN GROUP (ORDER BY SC_KEYNO) TYPE
								FROM	S_COMMON_CODE_SUB
								WHERE INSTR(BCD${column.KEYNO}.BD_DATA, SC_KEYNO, 1, 1)  > 0
							) COLUMN${column.KEYNO}	  



							,  REPLACE(BCD${column.KEYNO}.BD_DATA,'|',', ') COLUMN${column.KEYNO}


							,  BCD${column.KEYNO}.BD_DATA COLUMN${column.KEYNO}	



				FROM 		B_BOARD_NOTICE B
				LEFT JOIN 	U_USERINFO U
				ON			B.BN_REGNM	=	U.UI_KEYNO
	            LEFT JOIN   S_COMMON_FILE_SUB FS
           		ON          B.BN_THUMBNAIL = FS.FS_KEYNO

				LEFT JOIN 	B_BOARD_COLUMN_DATA BCD${column.KEYNO}
				ON			B.BN_KEYNO	=	BCD${column.KEYNO}.BD_BN_KEYNO AND BCD${column.KEYNO}.BD_BL_KEYNO = #{column.BL_KEYNO}


				LEFT JOIN	S_COMMON_CODE_SUB SC${column.KEYNO}
				ON			BCD${column.KEYNO}.BD_DATA = SC${column.KEYNO}.SC_KEYNO		


				WHERE 		BN_MN_KEYNO = #{MN_KEYNO}
				AND BN_DEL_YN = 'N'
				AND BN_USE_YN = 'Y'

				AND BN_PARENTKEY IS NULL
			)AA


				ORDER BY	${BMM_SORT_COLUMN} ${BMM_SORT_DIRECTION}


				ORDER BY	BN_KEYNO DESC


	)AAA

   	WHERE ROWNUM <= #{SIZE}

SELECT     BMM_FORM
FROM     B_BOARD_MAINMINI
WHERE     BMM_KEYNO = #{BMM_KEYNO}
